"use client"
import { useState, useCallback, useRef, forwardRef } from "react"
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from "react-leaflet"
import L from "leaflet"
import "leaflet/dist/leaflet.css"
import SearchLocation from "./SearchLocation"

L.Icon.Default.mergeOptions({
  iconRetinaUrl: "/images/marker-icon-2x.png",
  iconUrl: "/images/marker-icon.png",
  shadowUrl: "/images/marker-shadow.png",
})

const createIcon = (emoji) => L.divIcon({
  className: "custom-icon",
  html: `<div style="font-size: 32px;">${emoji}</div>`,
  iconSize: [32, 32],
  iconAnchor: [16, 16],
})

const icons = {
  crack: createIcon("üöß"), pothole: createIcon("‚ö†Ô∏è"), 
  photo: createIcon("üì∑"), search: createIcon("üìç")
}

const MapComponent = forwardRef(({ capturedPhotos = [], onDeletePhoto }, ref) => {
  const [markers, setMarkers] = useState([])
  const [selectedType, setSelectedType] = useState("crack")
  const mapRef = useRef()

  useImperativeHandle(ref, () => ({
    flyToLocation: (location) => mapRef.current?.setView(location, 16)
  }))

  const handleSearch = (coords, name) => {
    mapRef.current?.setView(coords, 16)
    L.marker(coords, { icon: icons.search })
      .addTo(mapRef.current)
      .bindPopup(`<b>Search Result:</b><br>${name}`)
      .openPopup().on("popupclose", (e) => e.target.remove())
  }

  const MapClickHandler = () => useMapEvents({
    click: (e) => !e.originalEvent.propagatedFromMarker && 
      setMarkers(prev => [...prev, {
        position: [e.latlng.lat, e.latlng.lng],
        type: selectedType,
        id: Date.now()
      }])
  })

  return (
    <div className="map-container">
      <MapContainer center={[19.125, 72.9]} zoom={13} ref={mapRef}
        style={{ height: "100%", width: "100%" }} tap={false}
        zoomControl={window.innerWidth > 768}>
        
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <MapClickHandler />

        {[...markers, ...capturedPhotos.map(p => ({
          id: p.id, type: 'photo', position: p.location
        }))].map(({ id, position, type }) => (
          <Marker key={id} position={position} icon={icons[type]}>
            <Popup>
              <b>{type.charAt(0).toUpperCase() + type.slice(1)}</b>
              <button onClick={() => {
                setMarkers(prev => prev.filter(m => m.id !== id))
                onDeletePhoto(id)
              }}>Delete</button>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      <div className="map-controls">
        <SearchLocation onSearch={handleSearch} />
        <select value={selectedType} onChange={e => setSelectedType(e.target.value)}>
          {["crack", "pothole"].map(o => 
            <option key={o} value={o}>{icons[o].options.html} {o}</option>
          )}
        </select>
        <button onClick={() => setMarkers(prev => prev.slice(0, -1))}>üóëÔ∏è Delete Last</button>
        <button onClick={() => setMarkers([])}>üßπ Clear All</button>
      </div>
    </div>
  )
})

export default MapComponent
